import React from 'react';
import { Link, useLocation } from 'react-router-dom';

// Mobile Navigation (Bootstrap)
export const Navigation = ({ currentView, setView }) => {
  const location = useLocation();
  return (
    <nav className="navbar navbar-expand bg-light fixed-bottom border-top shadow-sm d-md-none">
      <div className="container-fluid justify-content-around">
        <Link to="/" className={`nav-link text-center${location.pathname === '/' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-house-door-fill"></i><br/>Trang ch·ªß</Link>
        <Link to="/explore" className={`nav-link text-center${location.pathname === '/explore' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-search"></i><br/>Kh√°m ph√°</Link>
        <Link to="/blog" className={`nav-link text-center${location.pathname === '/blog' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-journal-text"></i><br/>Blog</Link>
        <Link to="/smart-chef" className={`nav-link text-center${location.pathname === '/smart-chef' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-robot"></i><br/>AI Chef</Link>
        <Link to="/profile" className={`nav-link text-center${location.pathname === '/profile' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-person-circle"></i><br/>H·ªì s∆°</Link>
      </div>
    </nav>
  );
};

// Desktop Sidebar (Bootstrap)
export const DesktopSidebar = () => {
  const location = useLocation();
  return (
    <div className="d-none d-md-flex flex-column bg-white border-end shadow-sm position-fixed h-100" style={{width: '250px', left: 0, top: 0, zIndex: 1040}}>
      <div className="p-4 border-bottom">
        <h1 className="fs-3 fw-bold text-warning d-flex align-items-center gap-2">
          <span className="fs-2">üç≤</span> B·∫øp Vi·ªát 4.0
        </h1>
      </div>
      <div className="flex-grow-1 py-4 px-3">
        <nav className="nav flex-column gap-2">
          <Link to="/" className={`nav-link${location.pathname === '/' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-house-door-fill me-2"></i>Trang ch·ªß</Link>
          <Link to="/explore" className={`nav-link${location.pathname === '/explore' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-search me-2"></i>Kh√°m ph√°</Link>
          <Link to="/blog" className={`nav-link${location.pathname === '/blog' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-journal-text me-2"></i>Blog & Review</Link>
          <Link to="/smart-chef" className={`nav-link${location.pathname === '/smart-chef' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-robot me-2"></i>AI Chef</Link>
          <Link to="/post" className={`nav-link${location.pathname === '/post' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-plus-circle me-2"></i>ƒêƒÉng b√†i</Link>
          <Link to="/profile" className={`nav-link${location.pathname === '/profile' ? ' active fw-bold text-primary' : ''}`}> <i className="bi bi-person-circle me-2"></i>H·ªì s∆°</Link>
        </nav>
      </div>
      <div className="p-3 border-top">
        <div className="d-flex align-items-center gap-3 p-2 rounded hover-bg-light cursor-pointer">
          <img src="https://picsum.photos/100/100" alt="Me" className="rounded-circle" style={{width: '40px', height: '40px'}} />
          <div className="d-flex flex-column">
            <span className="fw-semibold text-dark">Ng∆∞·ªùi d√πng</span>
            <span className="text-secondary small">ƒê·∫ßu b·∫øp t·∫°i gia</span>
          </div>
        </div>
      </div>
    </div>
  );
}
