import React, { useState } from "react";
import { Outlet, useNavigate } from "react-router-dom";
import { DesktopSidebar, Navigation } from "./Navigation";

const Layout = () => {
  const [currentView, setView] = useState("home");
  const navigate = useNavigate();

  React.useEffect(() => {
    if (currentView === "home") navigate("/");
    else if (currentView === "explore") navigate("/explore");
    else if (currentView === "blog") navigate("/blog");
    else if (currentView === "smart-chef") navigate("/smart-chef");
    else if (currentView === "post") navigate("/post");
    else if (currentView === "profile") navigate("/profile");
  }, [currentView, navigate]);

  return (
    <div className="min-h-screen flex bg-[#f8fafc]">
      {/* Desktop Sidebar */}
      {/* <DesktopSidebar currentView={currentView} setView={setView} /> */}

      {/* Main Content Area */}
      <main className="flex-1 md:ml-64 w-full transition-all duration-300">
        <Outlet />
      </main>

      {/* Mobile Bottom Navigation */}
      <Navigation currentView={currentView} setView={setView} />
    </div>
  );
};

export default Layout;
